import axios from 'axios';
export default {
  data() {
    return {
      content: '',
      selectedFile: null,
      token: 'your-jwt-token' // replace with actual token
    };
  },
  methods: {
    onFileChange(event) {
      this.selectedFile = event.target.files[0];
    },
    async submitPost() {
      if (!this.selectedFile || !this.content) {
        alert('Please fill out the content and select an image.');
        return;
      }
      const formData = new FormData();
      formData.append('file', this.selectedFile); // Ensure 'file' matches the key expected in your Flask route
      formData.append('content', this.content);
      try {
        const response = await axios.post('http://localhost:5000/api/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${this.token}` // Ensure the token is included
          },
          withCredentials: true // Add this line to ensure credentials are sent with the request
        });
        console.log('Post submitted successfully:', response.data);
      } catch (error) {
        console.error('Error submitting post:', error.response ? error.response.data : error.message);
      }
    }
  }
};
