import Vue from 'vue';
import Router from 'vue-router';
import HomePage from '@/views/HomePage.vue';
import AboutPage from '@/views/AboutPage.vue';
import LoginForm from '@/components/LoginForm.vue';
import SignupForm from '@/components/SignupForm.vue';
import UserDashboard from '@/components/UserDashboard.vue';
import CreatePost from '@/components/CreatePost.vue';
import UserProfile from '@/components/UserProfile.vue';
import store from '@/store'; // Ensure the store is correctly imported

Vue.use(Router);

const routes = [
  { path: '/', name: 'Home', component: HomePage },
  { path: '/about', name: 'About', component: AboutPage },
  { path: '/dashboard', name: 'UserDashboard', component: UserDashboard, meta: { requiresAuth: true } },
  { path: '/login', name: 'Login', component: LoginForm },
  { path: '/signup', name: 'Signup', component: SignupForm },
  { path: '/create-post', name: 'CreatePost', component: CreatePost, meta: { requiresAuth: true } },
  { path: '/profile', name: 'UserProfile', component: UserProfile, meta: { requiresAuth: true } }
];

const router = new Router({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
});

router.beforeEach(async (to, from, next) => {
  console.log(`Navigating to ${to.path} from ${from.path}.`);
  
  const isAuthenticated = store.getters.isAuthenticated;
  console.log(`Logged in: ${isAuthenticated}`);
  
  // Check if the user is authenticated by checking the token
  if (!isAuthenticated && localStorage.getItem('token')) {
    try {
      console.log('Fetching user data...');
      await store.dispatch('fetchUser');
      console.log('User data fetched successfully');
    } catch (error) {
      console.error('Failed to fetch user data:', error);
      store.commit('LOGOUT');
    }
  }

  // If the route requires authentication and the user is not authenticated
  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!store.getters.isAuthenticated) {
      console.log(`Access to ${to.path} requires auth. Redirecting to login.`);
      return next({ name: 'Login' });
    }
  }

  // If the user is trying to access login or signup page while authenticated
  if ((to.path === '/login' || to.path === '/signup') && store.getters.isAuthenticated) {
    console.log(`Already logged in. Redirecting to dashboard.`);
    return next({ name: 'UserDashboard' });
  }

  next();
});

export default router;
