import { ref } from 'vue';
export default {
  name: 'HomePage',
  setup() {
    const showStory = ref(false); // Modal visibility state

    const openStory = () => {
      showStory.value = true; // Open story modal
      document.body.style.overflow = 'hidden'; // Prevent background scroll
    };
    const closeStory = () => {
      showStory.value = false; // Close story modal
      document.body.style.overflow = 'auto'; // Restore background scroll
    };
    const scrollToContent = () => {
      const nextSection = document.querySelector('.services'); // Adjust if there is a section to scroll to
      if (nextSection) {
        nextSection.scrollIntoView({
          behavior: 'smooth'
        });
      }
    };
    return {
      showStory,
      openStory,
      closeStory,
      scrollToContent
    };
  }
};
