import Vue from 'vue';
import Vuex from 'vuex';
import axios from 'axios';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    token: null,
    user: null
  },
  mutations: {
    SET_TOKEN(state, token) {
      state.token = token;
    },
    SET_USER(state, user) {
      state.user = user;
    },
    CLEAR_AUTH(state) {
      state.token = null;
      state.user = null;
    }
  },
  actions: {
    async login({ commit }, credentials) {
      try {
        const response = await axios.post('http://127.0.0.1:5000/api/login', credentials);
        const token = response.data.token;
        localStorage.setItem('token', token);
        commit('SET_TOKEN', token);
        commit('SET_USER', response.data.user); // Assuming the login response also includes user data
        return response;
      } catch (error) {
        throw error;
      }
    },
    async logout({ commit }) {
      localStorage.removeItem('token');
      commit('CLEAR_AUTH');
    },
    checkAuthentication({ commit }) {
      const token = localStorage.getItem('token');
      if (token) {
        commit('SET_TOKEN', token);
        // Optionally, you can add code here to fetch the user data using the token
      } else {
        commit('CLEAR_AUTH');
      }
    }
  },
  getters: {
    isAuthenticated: state => !!state.token,
    getUser: state => state.user
  }
});
